INSERT INTO `doan-shopping`.author (id, introduce, name) VALUES (1, 'Ngô Tất Tố là một nhà văn, nhà báo, nhà Nho học, dịch giả và nhà nghiên cứu có ảnh hưởng lớn ở Việt Nam giai đoạn trước 1954.', 'Ngô Tất Tố');
INSERT INTO `doan-shopping`.author (id, introduce, name) VALUES (2, 'Vũ Trọng Phụng là một nhà văn, nhà báo nổi tiếng của Việt Nam vào đầu thế kỷ 20', 'Vũ Trọng Phụng');
INSERT INTO `doan-shopping`.author (id, introduce, name) VALUES (3, 'Phùng Quán là một nhà văn, nhà thơ Việt Nam, bắt đầu viết trong khoảng thời gian của cuộc chiến tranh Đông Dương và khẳng định được văn tài với Vượt Côn Đảo nhưng ông được biết đến nhiều hơn sau Đổi mới', 'Phùng Quán');
INSERT INTO `doan-shopping`.author (id, introduce, name) VALUES (4, 'Nhà xuất bản Giáo dục Việt Nam còn gọi là Nhà xuất bản Giáo dục, là một nhà xuất bản được sự quản lý của Nhà nước Việt Nam, trực thuộc Bộ Giáo dục và Đào tạo', 'NXB Giáo dục Việt Nam ');
INSERT INTO `doan-shopping`.author (id, introduce, name) VALUES (5, 'Victor Hugo là một nhà văn, nhà thơ, nhà viết kịch thuộc chủ nghĩa lãng mạn nổi tiếng của Pháp. Ông cũng đồng thời là một nhà chính trị, một trí thức dấn thân tiêu biểu của thế kỷ XIX. Victor Hugo chiếm một vị trí trang trọng trong lịch sử văn học Pháp.', 'Victor Hugo');
INSERT INTO `doan-shopping`.author (id, introduce, name) VALUES (6, 'William Shakespeare là một nhà văn và nhà viết kịch Anh, được coi là nhà văn vĩ đại nhất của Anh và là nhà viết kịch đi trước thời đại. Ông cũng được vinh danh là nhà thơ tiêu biểu của nước Anh và là "Thi sĩ của dòng sông Avon".', 'William Shakespeare');
INSERT INTO `doan-shopping`.author (id, introduce, name) VALUES (7, 'Văn học dân gian hay văn học truyền miệng là văn học được nói hoặc hát trái ngược với văn học được viết lại, mặc dù nhiều văn học truyền miệng đã được ghi lại bằng chữ viết.', 'Văn học dân gian');
INSERT INTO `doan-shopping`.author (id, introduce, name) VALUES (8, 'Lê Linh là một kiến trúc sư, họa sĩ truyện tranh Việt Nam, tác giả của bộ truyện tranh nổi tiếng Thần đồng đất Việt.', 'Lê Linh');