INSERT INTO `doan-shopping`.book_category (id, description, name) VALUES (1, 'Văn học là một loại hình sáng tác, tái hiện những vấn đề của đời sống xã hội và con người. Phương thức sáng tạo của văn học được thông qua sự hư cấu, cách thể hiện nội dung các đề tài được biểu hiện qua ngôn ngữ.', 'Văn học trong nước');
INSERT INTO `doan-shopping`.book_category (id, description, name) VALUES (2, 'Sách giáo khoa là loại sách cung cấp kiến thức, được biên soạn với mục đích dạy và học tại trường học.', 'Sách giáo khoa');
INSERT INTO `doan-shopping`.book_category (id, description, name) VALUES (3, 'Những tác phẩm viết về thiếu nhi và viết cho thiếu nhi do người lớn hoặc trẻ em sáng tác, chủ yếu được nhìn qua lăng kính trẻ thơ, có nội dung hướng đến giáo dục, bồi dưỡng tâm hồn, nhân cách trẻ em ở mọi lứa tuổi khác nhau.', 'Sách thiếu nhi');
INSERT INTO `doan-shopping`.book_category (id, description, name) VALUES (4, 'Văn học Ngôn ngữ Nước ngoài là phân của ngành Văn học, bao gồm Ngôn ngữ văn học Anh, Ngôn ngữ văn học Nga, Ngôn ngữ văn học Pháp, Ngôn ngữ văn học Đức, Ngôn ngữ văn học Nhật, Ngôn ngữ văn học Ấn Độ, Ngôn ngữ văn học Tây Ban Nha, Ngôn ngữ văn học Ả Rập, Ngôn ngữ Văn học châu Âu, Ngôn ngữ văn học Á Phi, Ngôn ngữ học và Ngôn ngữ ứng dụng nước ngoài.', 'Văn học nước ngoài');